const buttonSearch = document.querySelector(".button-search"),
  popup = document.querySelector(".modal-form"),
  arrivalDate = popup.querySelector(".arrival-date-input"),
  dateDeparture = popup.querySelector(".date-departure-input"),
  adult = popup.querySelector(".adult-input"),
  child = popup.querySelector(".child-input");
buttonSearch.addEventListener("click", function (e) {
  e.preventDefault(),
    popup.classList.remove("modal-show"),
    (popup.offsetWidth = popup.offsetWidth),
    popup.classList.add("modal-show"),
    popup.classList.toggle("visually-hidden");
}),
  popup.addEventListener("submit", function (e) {
    (arrivalDate.value && dateDeparture.value && adult.value && child.value) ||
      (e.preventDefault(),
      popup.classList.remove("modal-error"),
      (popup.offsetWidth = popup.offsetWidth),
      popup.classList.add("modal-error"));
  });
